
public class Actividad {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10, b = 3, d, e;
		float x, y;
		boolean c = true;
		x = a / b;
		c = a < b && c;
		d = a + b++;
		e = ++a - b;
		y = (float) a / b;
		
		System.out.println(x);
		System.out.println(c);
		System.out.println(d);
		System.out.println(e);
		System.out.println(y);
		
		

	}

}
